		this.validateBlocks.filter(':not(:eq('+this.currentStep+'))').addClass(this.options.hiddenClass);

		if(this.options.indicators) {
			this.indicators = this.holder.find(this.options.indicators);
			this.indicators.eq(0).addClass('active');
		}

		if(this.options.backBtn) this.backBtn = this.holder.find(this.options.backBtn);
